package com.acl.stock.config;import io.swagger.v3.oas.models.OpenAPI;import io.swagger.v3.oas.models.servers.Server;import org.springframework.beans.factory.annotation.Value;import org.springframework.stereotype.Component;import springfox.documentation.oas.web.OpenApiTransformationContext;import springfox.documentation.oas.web.WebMvcOpenApiTransformationFilter;import springfox.documentation.spi.DocumentationType;import javax.servlet.http.HttpServletRequest;import java.util.List;import java.util.Objects;@Componentpublic class SpringfoxSwaggerHostResolver implements WebMvcOpenApiTransformationFilter {    @Override    public OpenAPI transform(OpenApiTransformationContext<HttpServletRequest> context) {        OpenAPI swagger = context.getSpecification();        Server localServer = new Server();        localServer.setUrl("http://localhost:8081");        swagger.setServers(List.of(localServer));        return swagger;    }    @Override    public boolean supports(DocumentationType docType) {        return docType.equals(DocumentationType.OAS_30);    }}